import React, {Component} from 'react';
import Top from '../top/index';
import Tile from '../common/tile/index';
import {tilesPacient} from '../../scripts/index'
import {tilesDoctor} from '../../scripts/index'
import './index.css';

export default class Main extends Component {

  constructor(props) {
    super(props);
    this.state = {
      isDoctor: 0
    }
  }

  renderTileList() {
    var tileList = [];
    var tileProperties;
    tileProperties = this.state.isDoctor ?  tilesPacient : tilesDoctor;
    for (let i = 0; i < tilesPacient.length; i++) {
      console.log(tileProperties[i].faIcon);
      tileList.push(<Tile key={i} icon={tileProperties[i].faIcon} description={tileProperties[i].description}
                          background={tileProperties[i].backgroundImage} redirect={tileProperties[i].link}/>)
    }
    return tileList;
  }

  render() {
    return (
        <div className='project-main'>
          <Top/>
          <div className='tile-wrapper'>
            {this.renderTileList()}
          </div>
        </div>

    )
  }
}